import React, { useState } from "react";

const CreditCard = ({ cardNumber, name, expiry, cvv }) => {
  const [showFullNumber, setShowFullNumber] = useState(false);

  const toggleShowFullNumber = () => {
    setShowFullNumber(!showFullNumber);
  };

  const formatCardNumber = (number) => {
    // Format card number (e.g., "1234 5678 9012 3456")
    return number.replace(/\d{4}(?=.)/g, "$& ");
  };

  const last4Digits = (number) => {
    // Extract last 4 digits of card number
    return number.slice(-4);
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <div className="flex justify-between items-center mb-4">
        <img
          class="relative object-cover w-full h-full rounded-xl"
          src="https://i.imgur.com/kGkSg1v.png"
        ></img>
        <button
          className="text-blue-500 hover:underline focus:outline-none"
          onClick={toggleShowFullNumber}
        >
          {showFullNumber ? "Hide Number" : "Show Number"}
        </button>
      </div>
      <div className="flex items-center mb-4">
        <img src="/card-logo.png" alt="Card Logo" className="h-8 mr-4" />
        <span className="text-xl">
          {showFullNumber ? formatCardNumber(cardNumber) : "XXXX XXXX XXXX "}
          {last4Digits(cardNumber)}
        </span>
      </div>
      <div className="flex justify-between">
        <div>
          <p className="mb-2">Cardholder Name</p>
          <p>{name}</p>
        </div>
        <div>
          <p className="mb-2">Expiry</p>
          <p>{expiry}</p>
        </div>
        <div>
          <p className="mb-2">CVV</p>
          <p>{cvv}</p>
        </div>
      </div>
    </div>
  );
};

const App = () => {
  const cardNumber = "1234567890123456"; // Placeholder credit card number
  const name = "John Doe"; // Placeholder cardholder name
  const expiry = "12/24"; // Placeholder expiry date
  const cvv = "123"; // Placeholder CVV

  return (
    <div className="container mx-auto px-4">
      <CreditCard
        cardNumber={cardNumber}
        name={name}
        expiry={expiry}
        cvv={cvv}
      />
    </div>
  );
};

export default App;
